import React, { useState, useCallback, useRef, useEffect } from "react";
import { NET_LOCATION } from "../../util/location";

const { kakao } = window;

const PlaceInfo = (place) => {
  return (
    <div
      className="placeinfo_wrap"
      onMouseDown={kakao.maps.event.preventMap}
      onTouchStart={kakao.maps.event.preventMap}
    >
      <div className="placeinfo">
        <a
          className="title"
          href="#!"
          //   href={place.place_url}
          target={"_blank"}
          title={place.place_name}
        >
          {place.place_name}
        </a>
        {place.road_address_name && (
          <span title={`${place.road_address_name}`}>
            {place.road_address_name}
          </span>
        )}
        <span className="jibun" title={`${place.address_name}`}>
          지번 : {place.address_name}
        </span>
        <span className="tel">{place.phone}</span>
      </div>
      <div className="after"></div>
    </div>
  );
};

const CoffeeMap = () => {
  const mapRef = useRef(null);
  const [markers, setMarkers] = useState([]); // 마커를 담을 배열
  const [map, setMap] = useState(null);
  const [ps, setPs] = useState(null);

  const currCategory = "CE7"; // 카페 카테고리 코드

  let placeOverlay = new kakao.maps.CustomOverlay({ zIndex: 1 });

  // 마커 초기화 함수
  function removeMarker() {
    setMarkers([]);
  }
  const displayPlaceInfo = useCallback(
    (place) => {
      placeOverlay.setContent(PlaceInfo(place));
      placeOverlay.setPosition(new kakao.maps.LatLng(place.y, place.x));
      placeOverlay.setMap(map);
    },
    [map, placeOverlay],
  );

  // 마커를 생성하고 지도 위에 마커를 표시하는 함수입니다
  const addMarker = useCallback(
    (position, order) => {
      const imageSrc =
        "https://t1.daumcdn.net/localimg/localimages/07/mapapidoc/places_category.png"; // 마커 이미지 url, 스프라이트 이미지를 씁니다
      const imageSize = new kakao.maps.Size(27, 28); // 마커 이미지의 크기
      const imgOptions = {
        spriteSize: new kakao.maps.Size(72, 208), // 스프라이트 이미지의 크기
        spriteOrigin: new kakao.maps.Point(46, order * 36), // 스프라이트 이미지 중 사용할 영역의 좌상단 좌표
        offset: new kakao.maps.Point(11, 28), // 마커 좌표에 일치시킬 이미지 내에서의 좌표
      };
      const markerImage = new kakao.maps.MarkerImage(
        imageSrc,
        imageSize,
        imgOptions,
      );
      const marker = new kakao.maps.Marker({
        position: position, // 마커의 위치
        image: markerImage,
      });

      marker.setMap(map); // 지도 위에 마커를 표출합니다
      setMarkers(marker); // 배열에 생성된 마커를 추가합니다

      return marker;
    },
    [map],
  );

  // 지도에 마커를 표출하는 함수입니다
  const displayPlaces = useCallback(
    (places) => {
      // 몇번째 카테고리가 선택되어 있는지 얻어옵니다
      // 이 순서는 스프라이트 이미지에서의 위치를 계산하는데 사용됩니다
      var order = document
        .getElementById(currCategory)
        .getAttribute("data-order");

      for (var i = 0; i < places.length; i++) {
        // 마커를 생성하고 지도에 표시합니다
        var marker = addMarker(
          new kakao.maps.LatLng(places[i].y, places[i].x),
          order,
        );

        // 마커와 검색결과 항목을 클릭 했을 때
        // 장소정보를 표출하도록 클릭 이벤트를 등록합니다
        (function (marker, place) {
          kakao.maps.event.addListener(marker, "click", function () {
            displayPlaceInfo(place);
          });
        })(marker, places[i]);
      }
    },
    [addMarker, displayPlaceInfo],
  );

  // 장소검색이 완료됐을 때 호출되는 콜백함수 입니다
  const placesSearchCB = useCallback(
    (data, status, pagination) => {
      if (status === kakao.maps.services.Status.OK) {
        // 정상적으로 검색이 완료됐으면 지도에 마커를 표출합니다
        displayPlaces(data);
      } else if (status === kakao.maps.services.Status.ZERO_RESULT) {
        // 검색결과가 없는경우 해야할 처리가 있다면 이곳에 작성해 주세요
      } else if (status === kakao.maps.services.Status.ERROR) {
        // 에러로 인해 검색결과가 나오지 않은 경우 해야할 처리가 있다면 이곳에 작성해 주세요
      }
    },
    [displayPlaces],
  );

  // 카테고리 검색을 요청하는 함수입니다
  const searchPlaces = useCallback(() => {
    // 커스텀 오버레이를 숨깁니다
    placeOverlay.setMap(null);

    // 지도에 표시되고 있는 마커를 제거합니다
    removeMarker();

    ps.categorySearch(currCategory, placesSearchCB, { useMapBounds: true });
  }, [placeOverlay, placesSearchCB, ps]);

  // 카카오 맵 생성
  useEffect(() => {
    const container = mapRef.current;
    const options = {
      center: new kakao.maps.LatLng(NET_LOCATION.let, NET_LOCATION.lon),
      level: 3,
    };
    const map = new kakao.maps.Map(container, options);
    setMap(map);
    const ps = new kakao.maps.services.Places(map);
    setPs(ps);
    kakao.maps.event.addListener(map, "idle", searchPlaces);
  }, [searchPlaces]);

  return <div ref={mapRef}>{/* <PlaceInfo /> */}</div>;
};

export default CoffeeMap;
