import react, { useState, useRef, useEffect, createElement } from "react";
import KakaoMapScript from "../../util/kakaoMapScript";
import NET_LOCATION from "../../util/location";

const CoffeeMap = () => {
  const mapRef = useRef(null);
  const [marker, setMarker] = useState([]); // 마커를 담을 배열
  const [map, setMap] = useState(null);
  const placeOverlay = new KakaoMapScript.maps.CustomOverlay({ zIndex: 1 });
  const CATEGORY_CODE = "CE7"; // 카페 카테고리 코드
  const contentNode = createElement("div");

  // 마커 초기화 함수
  function removeMarker() {
    setMarker([]);
  }

  // 엘리먼트에 이벤트 핸들러를 등록하는 함수입니다
  function addEventHandle(target, type, callback) {
    if (target.addEventListener) {
      target.addEventListener(type, callback);
    } else {
      target.attachEvent("on" + type, callback);
    }
  }

  // 카카오 맵 생성
  useEffect(() => {
    const container = mapRef.current;
    const options = {
      center: new KakaoMapScript.maps.LatLng(
        NET_LOCATION.let,
        NET_LOCATION.lon,
      ),
    };
    const map = new KakaoMapScript.maps.Map(container, options);
    setMap(map);
  }, []);

  return <div ref={mapRef}>Map</div>;
};

export default CoffeeMap;
