import React, { useState } from "react";
import styled from "styled-components";

const FoodList = ({ place, setPlace, list }) => {
  console.log(place);
  console.log(list);

  const [like, setLike] = useState([]);
  const [unlike, setUnLike] = useState([]);

  const [rank, setRank] = useState({});

  const handleRank = (item) => {
    setRank((prev) => ({
      ...prev,
      [item.id]: !rank[item.id] ? 1 : prev[item.id]++,
    }));
  };
  return (
    <Container>
      {list.length !== 0 && (
        <Ul>
          {list.map((v, index) => {
            return (
              <Li key={index} onClick={() => setPlace(v)}>
                <span>{v.place_name}</span>
                <div>
                  <LikeButton
                    includes={like.includes(v.id)}
                    onClick={() =>
                      setLike((prev) =>
                        !prev.includes(v.id) ? [...prev, v.id] : prev,
                      )
                    }
                  >
                    üëç like
                  </LikeButton>{" "}
                  <UnLikeButton
                    includes={unlike.includes(v.id)}
                    onClick={() => {
                      setUnLike((prev) =>
                        !prev.includes(v.id) ? [...prev, v.id] : prev,
                      );
                      like.includes(v.id) &&
                        setLike((prev) => prev.filter((item) => item !== v.id));
                    }}
                  >
                    üëé unlike
                  </UnLikeButton>
                </div>
              </Li>
            );
          })}
        </Ul>
      )}
      {place && (
        <PlaceContainer>
          <div>{place.address_name}</div>
          <div>{place.category_name}</div>
          <div>{place.place_name}</div>
          <div>{place.phone}</div>
          <div>{place.road_address_name}</div>
        </PlaceContainer>
      )}
    </Container>
  );
};

export default FoodList;

const Container = styled.div`
  width: 100%;
  display: flex;
`;

const Ul = styled.ul`
  padding: 0;
  margin: 0px 10px;
`;
const Li = styled.li`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 4px;
  margin-bottom: 8px;
  background: white;
  border-radius: 4px;
  cursor: pointer;
`;

const LikeButton = styled.button`
  border-radius: 4px;
  padding: 2px 6px;
  margin: 2px;
  background: #b2d8fb; //#3392ff;
  border: none;
  color: white;
  cursor: pointer;
`;

const UnLikeButton = styled.button`
  border-radius: 4px;
  padding: 2px 6px;
  margin: 2px;
  background: #fbbab2; //#ff4533;
  border: none;
  color: white;
  cursor: pointer;
`;

const PlaceContainer = styled.div``;
