import React from "react";
import react, { useState, useRef, useEffect, createElement } from "react";
import KakaoMapScript from "../../util/kakaoMapScript";
import NET_LOCATION from "../../util/location";

const { kakao } = window;

const PlaceInfo = (place) => {
  return (
    <React.Fragment>
      <div className="placeinfo">
        <a
          className="title"
          href={place.place_url}
          target={"_blank"}
          title={place.place_name}
        >
          {place.place_name}
        </a>
        {place.road_address_name && (
          <span title={`${place.road_address_name}`}>
            {place.road_address_name}
          </span>
        )}
        <span className="jibun" title={`${place.address_name}`}>
          지번 : {place.address_name}
        </span>
        <span className="tel">{place.phone}</span>
      </div>
      <div className="after"></div>
    </React.Fragment>
  );
};

const CoffeeMap = () => {
  const mapRef = useRef(null);
  const [marker, setMarker] = useState([]); // 마커를 담을 배열
  const [map, setMap] = useState(null);
  const placeOverlay = new KakaoMapScript.maps.CustomOverlay({ zIndex: 1 });
  const CATEGORY_CODE = "CE7"; // 카페 카테고리 코드
  const contentNode = createElement("div");

  // 마커 초기화 함수
  function removeMarker() {
    setMarker([]);
  }

  // 엘리먼트에 이벤트 핸들러를 등록하는 함수입니다
  function addEventHandle(target, type, callback) {
    if (target.addEventListener) {
      target.addEventListener(type, callback);
    } else {
      target.attachEvent("on" + type, callback);
    }
  }

  useEffect(() => {
    // 커스텀 오버레이의 컨텐츠 노드에 mousedown, touchstart 이벤트가 발생했을때
    // 지도 객체에 이벤트가 전달되지 않도록 이벤트 핸들러로 kakao.maps.event.preventMap 메소드를 등록합니다
    addEventHandle(contentNode, "mousedown", kakao.maps.event.preventMap);
    addEventHandle(contentNode, "touchstart", kakao.maps.event.preventMap);
  }, []);

  // 카카오 맵 생성
  useEffect(() => {
    const container = mapRef.current;
    const options = {
      center: new KakaoMapScript.maps.LatLng(
        NET_LOCATION.let,
        NET_LOCATION.lon,
      ),
    };
    const map = new KakaoMapScript.maps.Map(container, options);
    setMap(map);
  }, []);

  return <div ref={mapRef}>Map</div>;
};

export default CoffeeMap;
