import React, { useState } from "react";
import styled from "styled-components";

const FoodList = ({ place, setPlace, list }) => {
  console.log(place);
  console.log(list);

  const [like, setLike] = useState(0);
  const [unlike, setUnLike] = useState(0);

  const [rank, setRank] = useState({});

  const handleRank = (item) => {
    setRank((prev) => ({
      ...prev,
      [item.id]: !rank[item.id] ? 1 : prev[item.id]++,
    }));
  };
  return (
    <Container>
      {list.length !== 0 && (
        <Ul>
          {list.map((v, index) => {
            return (
              <Li key={index} onClick={() => setPlace(v)}>
                <span>{v.place_name}</span>
                <div>
                  <LikeButton onClick={() => handleRank(v)}>üëç like</LikeButton>{" "}
                  : {like} <UnLikeButton>üëé unlike</UnLikeButton> : {unlike}
                </div>
              </Li>
            );
          })}
        </Ul>
      )}
      {place && (
        <PlaceContainer>
          <div>{place.address_name}</div>
          <div>{place.category_name}</div>
          <div>{place.place_name}</div>
          <div>{place.phone}</div>
          <div>{place.road_address_name}</div>
        </PlaceContainer>
      )}
    </Container>
  );
};

export default FoodList;

const Container = styled.div`
  width: 100%;
  display: flex;
`;

const Ul = styled.ul`
  padding: 0;
  margin: 0px 10px;
`;
const Li = styled.li`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 4px;
  margin-bottom: 8px;
  background: white;
  border-radius: 4px;
  cursor: pointer;
`;

const LikeButton = styled.button`
  border-radius: 4px;
  padding: 2px 6px;
  margin: 2px;
  background: #b2d8fb; //#3392ff;
  border: none;
  color: white;
  cursor: pointer;
`;

const UnLikeButton = styled.button`
  border-radius: 4px;
  padding: 2px 6px;
  margin: 2px;
  background: #fbbab2; //#ff4533;
  border: none;
  color: white;
  cursor: pointer;
`;

const PlaceContainer = styled.div``;
